import * as React from 'react';
import { Edit, TextInput, BooleanInput } from 'react-admin';
import Box from '@material-ui/core/Box';
import { useStyles } from './useStyles';
import { validateCategoryCreation } from './categoryValidation';
import SimpleForm from 'components/SimpleForm';

export const CategoryEdit = (props: any) => {
  const classes = useStyles();

  return (
    <Edit {...props} undoable={false}>
      <SimpleForm validate={validateCategoryCreation}>
        <Box className={classes.fields}>
          <TextInput className={classes.input} fullWidth source="code" />
          <TextInput className={classes.input} fullWidth source="description" />
        </Box>

      </SimpleForm>
    </Edit>
  );
};
